CREATE DEFINER=`root`@`localhost` FUNCTION `CalculateLateFee` (`due_date` DATE, `return_date` DATE) RETURNS INT(11) DETERMINISTIC READS SQL DATA BEGIN
    DECLARE days_late INT DEFAULT 0;
    DECLARE actual_return_date DATE;
    
    -- Utiliser la date de retour effective ou la date actuelle si pas encore retourné
    SET actual_return_date = IFNULL(return_date, CURDATE());
    
    -- Calculer les jours de retard
    IF actual_return_date > due_date THEN
        SET days_late = DATEDIFF(actual_return_date, due_date);
    END IF;
    
    -- Retourner les frais (100 FCFA par jour de retard)
    RETURN days_late * 100;
END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `generate_barcode` (`doc_type` VARCHAR(20), `doc_year` INT) RETURNS VARCHAR(50) CHARSET utf8mb4 COLLATE utf8mb4_general_ci DETERMINISTIC READS SQL DATA BEGIN
    DECLARE barcode_result VARCHAR(50);
    DECLARE prefix_val VARCHAR(10);
    DECLARE sequence_num INT;
    
    -- Récupérer le préfixe
    SELECT prefix INTO prefix_val
    FROM barcode_config 
    WHERE document_type = doc_type AND is_active = TRUE;
    
    -- Récupérer le prochain numéro de séquence
    SET sequence_num = get_next_sequence(doc_type, doc_year);
    
    -- Générer le code-barres selon le type
    CASE doc_type
        WHEN 'book' THEN
            SET barcode_result = CONCAT(prefix_val, LPAD(sequence_num, 6, '0'));
        WHEN 'thesis' THEN
            SET barcode_result = CONCAT(prefix_val, doc_year, LPAD(sequence_num, 3, '0'));
        WHEN 'memoire' THEN
            SET barcode_result = CONCAT(prefix_val, doc_year, LPAD(sequence_num, 3, '0'));
        WHEN 'stage_report' THEN
            SET barcode_result = CONCAT(prefix_val, doc_year, LPAD(sequence_num, 3, '0'));
        ELSE
            SET barcode_result = CONCAT(prefix_val, doc_year, LPAD(sequence_num, 4, '0'));
    END CASE;
    
    RETURN barcode_result;
END$$

