CREATE DEFINER=`root`@`localhost` FUNCTION `get_next_sequence` (`doc_type` VARCHAR(20), `doc_year` INT) RETURNS INT(11) DETERMINISTIC READS SQL DATA BEGIN
    DECLARE next_seq INT DEFAULT 1;
    
    -- Insérer ou mettre à jour la séquence
    INSERT INTO document_sequences (document_type, year, last_sequence)
    VALUES (doc_type, doc_year, 1)
    ON DUPLICATE KEY UPDATE 
        last_sequence = last_sequence + 1;
    
    -- Récupérer la nouvelle séquence
    SELECT last_sequence INTO next_seq
    FROM document_sequences 
    WHERE document_type = doc_type AND year = doc_year;
    
    RETURN next_seq;
END$$

