--
-- Déclencheurs `books`
--
DELIMITER $$
CREATE TRIGGER `books_generate_codes` BEFORE INSERT ON `books` FOR EACH ROW BEGIN
    DECLARE seq_num INT;
    DECLARE current_year INT DEFAULT YEAR(NOW());
    
    -- Générer les codes seulement s'ils ne sont pas fournis
    IF NEW.barcode IS NULL THEN
        SET NEW.barcode = generate_barcode('book', current_year);
        
        -- Récupérer le numéro de séquence utilisé
        SELECT last_sequence INTO seq_num
        FROM document_sequences 
        WHERE document_type = 'book' AND year = current_year;
        
        -- Générer les autres identifiants
        SET NEW.cames_id = generate_cames_id('book', current_year, seq_num);
        SET NEW.local_id = generate_local_id('book', current_year, seq_num);
        SET NEW.handle = generate_handle('book', current_year, seq_num);
    END IF;
END
$$
DELIMITER ;

