DELIMITER $$
CREATE TRIGGER `memoires_generate_codes` BEFORE INSERT ON `memoires` FOR EACH ROW BEGIN
    DECLARE seq_num INT;
    DECLARE doc_year INT DEFAULT YEAR(NOW());
    
    -- Extraire l'année de academic_year si disponible (format: "2024-2025")
    IF NEW.academic_year IS NOT NULL THEN
        SET doc_year = CAST(SUBSTRING(NEW.academic_year, 1, 4) AS UNSIGNED);
    END IF;
    
    IF NEW.barcode IS NULL THEN
        SET NEW.barcode = generate_barcode('memoire', doc_year);
        
        SELECT last_sequence INTO seq_num
        FROM document_sequences 
        WHERE document_type = 'memoire' AND year = doc_year;
        
        SET NEW.cames_id = generate_cames_id('memoire', doc_year, seq_num);
        SET NEW.local_id = generate_local_id('memoire', doc_year, seq_num);
        SET NEW.handle = generate_handle('memoire', doc_year, seq_num);
    END IF;
END
$$
DELIMITER ;

