DELIMITER $$
CREATE TRIGGER `stage_reports_generate_codes` BEFORE INSERT ON `stage_reports` FOR EACH ROW BEGIN
    DECLARE seq_num INT;
    DECLARE doc_year INT DEFAULT YEAR(NOW());
    
    -- Extraire l'année de academic_year si disponible
    IF NEW.academic_year IS NOT NULL THEN
        SET doc_year = CAST(SUBSTRING(NEW.academic_year, 1, 4) AS UNSIGNED);
    END IF;
    
    IF NEW.barcode IS NULL THEN
        SET NEW.barcode = generate_barcode('stage_report', doc_year);
        
        SELECT last_sequence INTO seq_num
        FROM document_sequences 
        WHERE document_type = 'stage_report' AND year = doc_year;
        
        SET NEW.cames_id = generate_cames_id('stage_report', doc_year, seq_num);
        SET NEW.local_id = generate_local_id('stage_report', doc_year, seq_num);
        SET NEW.handle = generate_handle('stage_report', doc_year, seq_num);
    END IF;
END
$$
DELIMITER ;

