--
-- Structure de la table `theses`
--

CREATE TABLE `theses` (
  `id` char(36) NOT NULL,
  `main_author` text NOT NULL COMMENT 'Auteur de la thèse',
  `student_id` varchar(50) DEFAULT NULL COMMENT 'Matricule étudiant',
  `director` text NOT NULL COMMENT 'Directeur de thèse',
  `co_director` text DEFAULT NULL COMMENT 'Co-directeur de thèse',
  `title` text NOT NULL COMMENT 'Titre de la thèse',
  `target_degree` text NOT NULL COMMENT 'Diplôme visé (Master, Doctorat, etc.)',
  `specialty` text DEFAULT NULL COMMENT 'Spécialité/Discipline',
  `defense_year` int(11) DEFAULT NULL COMMENT 'Année de soutenance',
  `defense_date` date DEFAULT NULL COMMENT 'Date exacte de soutenance',
  `university` text DEFAULT NULL COMMENT 'Université de soutenance',
  `faculty` text DEFAULT NULL COMMENT 'Faculté/École',
  `department` text DEFAULT NULL COMMENT 'Département',
  `pagination` text DEFAULT NULL COMMENT 'Nombre de pages',
  `summary` text DEFAULT NULL COMMENT 'Résumé de la thèse',
  `keywords` longtext DEFAULT NULL COMMENT 'Mots-clés en français' CHECK (json_valid(`keywords`)),
  `abstract` text DEFAULT NULL COMMENT 'Abstract en anglais',
  `keywords_en` longtext DEFAULT NULL COMMENT 'Mots-clés en anglais' CHECK (json_valid(`keywords_en`)),
  `document_path` text DEFAULT NULL COMMENT 'Chemin vers le fichier PDF/Word',
  `document_type` enum('pdf','doc','docx') DEFAULT NULL COMMENT 'Type de document',
  `document_size` int(11) DEFAULT NULL COMMENT 'Taille du fichier en octets',
  `is_accessible` tinyint(1) DEFAULT 1 COMMENT 'Document accessible au public',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `available_copies` int(11) NOT NULL DEFAULT 1 COMMENT 'Exemplaires disponibles',
  `total_copies` int(11) NOT NULL DEFAULT 1 COMMENT 'Total des exemplaires',
  `dewey_classification` varchar(20) DEFAULT NULL COMMENT 'Classification Dewey (ex: 004.6)',
  `cdu_classification` varchar(30) DEFAULT NULL COMMENT 'Classification Décimale Universelle (ex: 004.4''273)',
  `subject_headings` longtext DEFAULT NULL COMMENT 'Vedettes-matières en format JSON' CHECK (json_valid(`subject_headings`)),
  `barcode` varchar(50) DEFAULT NULL COMMENT 'Code-barres unique de la thèse (THE2024001)',
  `cames_id` varchar(100) DEFAULT NULL COMMENT 'Identifiant CAMES/DICAMES (CAMES-CM-UDM-2024-TH-001)',
  `local_id` varchar(50) DEFAULT NULL COMMENT 'Identifiant local lisible (UDM-2024-TH-001)',
  `handle` varchar(100) DEFAULT NULL COMMENT 'Handle persistant (123456789/udm-2024-th-001)',
  `doi` varchar(100) DEFAULT NULL COMMENT 'DOI pour publications (10.1000/udm.2024.th.001)',
  `status` enum('available','borrowed','reserved','lost','damaged','withdrawn','not_for_loan','in_transit','in_processing','missing') DEFAULT 'available' COMMENT 'Statut du document selon standards SIGB',
  `target_audience` enum('undergraduate','graduate','postgraduate','researcher','professional','academic') DEFAULT 'graduate' COMMENT 'Public cible selon standards SIGB',
  `format` enum('print','digital','pdf','bound','electronic','multimedia') DEFAULT 'print' COMMENT 'Format selon standards SIGB',
  `language` varchar(10) DEFAULT 'fr' COMMENT 'Langue du document (ISO 639-1)',
  `physical_location` varchar(255) DEFAULT NULL COMMENT 'Localisation physique (rayon, section)',
  `view_count` int(11) DEFAULT 0 COMMENT 'Nombre de consultations',
  `institution` varchar(255) DEFAULT 'Université des Montagnes' COMMENT 'Institution de soutenance',
  `country` varchar(100) DEFAULT 'Cameroun' COMMENT 'Pays de soutenance',
  `access_rights` enum('open','restricted','embargo') DEFAULT 'open' COMMENT 'Droits d''accès au document',
  `license` enum('CC-BY','CC-BY-SA','All Rights Reserved') DEFAULT 'CC-BY' COMMENT 'Licence du document'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Déclencheurs `theses`
--
DELIMITER $$
CREATE TRIGGER `theses_generate_codes` BEFORE INSERT ON `theses` FOR EACH ROW BEGIN
    DECLARE seq_num INT;
    DECLARE doc_year INT DEFAULT COALESCE(NEW.defense_year, YEAR(NOW()));
    
    IF NEW.barcode IS NULL THEN
        SET NEW.barcode = generate_barcode('thesis', doc_year);
        
        SELECT last_sequence INTO seq_num
        FROM document_sequences 
        WHERE document_type = 'thesis' AND year = doc_year;
        
        SET NEW.cames_id = generate_cames_id('thesis', doc_year, seq_num);
        SET NEW.local_id = generate_local_id('thesis', doc_year, seq_num);
        SET NEW.handle = generate_handle('thesis', doc_year, seq_num);
    END IF;
END
$$
DELIMITER ;

