CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `academic_documents`  AS SELECT 'these' AS `document_type`, `theses`.`id` AS `id`, `theses`.`title` AS `title`, coalesce(nullif(`theses`.`main_author`,''),'Auteur non spécifié') AS `author`, coalesce(nullif(`theses`.`director`,''),'Directeur non spécifié') AS `supervisor`, coalesce(nullif(`theses`.`target_degree`,''),'Doctorat') AS `degree`, coalesce(nullif(`theses`.`specialty`,''),'Non spécifiée') AS `specialty`, coalesce(`theses`.`defense_year`,year(`theses`.`created_at`)) AS `year`, `theses`.`defense_date` AS `defense_date`, coalesce(nullif(`theses`.`university`,''),'Université non spécifiée') AS `university`, coalesce(nullif(`theses`.`faculty`,''),'Faculté non spécifiée') AS `faculty`, `theses`.`document_path` AS `document_path`, `theses`.`document_type` AS `file_type`, `theses`.`document_size` AS `document_size`, `theses`.`is_accessible` AS `is_accessible`, `theses`.`created_at` AS `created_at` FROM `theses`union all select 'memoire' AS `document_type`,`memoires`.`id` AS `id`,`memoires`.`title` AS `title`,coalesce(nullif(`memoires`.`main_author`,''),'Auteur non spécifié') AS `author`,coalesce(nullif(`memoires`.`supervisor`,''),'Superviseur non spécifié') AS `supervisor`,case when `memoires`.`degree_level` is not null and `memoires`.`degree_level` <> '' then `memoires`.`degree_level` else 'Master' end AS `degree`,case when `memoires`.`specialty` is not null and `memoires`.`specialty` <> '' then `memoires`.`specialty` when `memoires`.`field_of_study` is not null and `memoires`.`field_of_study` <> '' then `memoires`.`field_of_study` else 'Informatique' end AS `specialty`,case when `memoires`.`academic_year` is not null and `memoires`.`academic_year` regexp '^[0-9]{4}-[0-9]{4}$' then substr(`memoires`.`academic_year`,1,4) when `memoires`.`defense_date` is not null then year(`memoires`.`defense_date`) else year(`memoires`.`created_at`) end AS `year`,`memoires`.`defense_date` AS `defense_date`,coalesce(nullif(`memoires`.`university`,''),'Université non spécifiée') AS `university`,coalesce(nullif(`memoires`.`faculty`,''),'Faculté non spécifiée') AS `faculty`,`memoires`.`document_path` AS `document_path`,`memoires`.`document_type` AS `file_type`,`memoires`.`document_size` AS `document_size`,`memoires`.`is_accessible` AS `is_accessible`,`memoires`.`created_at` AS `created_at` from `memoires` union all select 'rapport_stage' AS `document_type`,`stage_reports`.`id` AS `id`,`stage_reports`.`title` AS `title`,coalesce(nullif(`stage_reports`.`student_name`,''),'Étudiant non spécifié') AS `author`,coalesce(nullif(`stage_reports`.`supervisor`,''),'Superviseur non spécifié') AS `supervisor`,case when `stage_reports`.`degree_level` is not null and `stage_reports`.`degree_level` <> '' then `stage_reports`.`degree_level` else 'Licence' end AS `degree`,case when `stage_reports`.`specialty` is not null and `stage_reports`.`specialty` <> '' then `stage_reports`.`specialty` when `stage_reports`.`field_of_study` is not null and `stage_reports`.`field_of_study` <> '' then `stage_reports`.`field_of_study` else 'Stage Professionnel' end AS `specialty`,case when `stage_reports`.`academic_year` is not null and `stage_reports`.`academic_year` regexp '^[0-9]{4}-[0-9]{4}$' then substr(`stage_reports`.`academic_year`,1,4) when `stage_reports`.`stage_start_date` is not null then year(`stage_reports`.`stage_start_date`) else year(`stage_reports`.`created_at`) end AS `year`,`stage_reports`.`defense_date` AS `defense_date`,coalesce(nullif(`stage_reports`.`university`,''),'Université non spécifiée') AS `university`,coalesce(nullif(`stage_reports`.`faculty`,''),'Faculté non spécifiée') AS `faculty`,`stage_reports`.`document_path` AS `document_path`,`stage_reports`.`document_type` AS `file_type`,`stage_reports`.`document_size` AS `document_size`,`stage_reports`.`is_accessible` AS `is_accessible`,`stage_reports`.`created_at` AS `created_at` from `stage_reports`  ;

