-- Structure de la table `system_logs`
--

CREATE TABLE `system_logs` (
  `id` int(11) NOT NULL,
  `action` varchar(100) NOT NULL COMMENT 'Type d''action (login, logout, create, update, delete, etc.)',
  `table_name` varchar(100) DEFAULT NULL COMMENT 'Table concernée par l''action',
  `record_id` varchar(100) DEFAULT NULL COMMENT 'ID de l''enregistrement concerné',
  `user_id` char(36) DEFAULT NULL COMMENT 'ID de l''utilisateur qui a effectué l''action',
  `user_email` varchar(255) DEFAULT NULL COMMENT 'Email de l''utilisateur',
  `ip_address` varchar(45) DEFAULT NULL COMMENT 'Adresse IP',
  `user_agent` text DEFAULT NULL COMMENT 'User Agent du navigateur',
  `message` text NOT NULL COMMENT 'Message de log',
