  `context` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT 'Contexte additionnel en JSON' CHECK (json_valid(`context`)),
  `session_id` varchar(255) DEFAULT NULL COMMENT 'ID de session',
  `request_method` enum('GET','POST','PUT','DELETE','PATCH') DEFAULT NULL COMMENT 'Méthode HTTP',
  `request_url` text DEFAULT NULL COMMENT 'URL de la requête',
  `response_status` int(11) DEFAULT NULL COMMENT 'Code de statut HTTP',
  `execution_time` decimal(10,3) DEFAULT NULL COMMENT 'Temps d''exécution en millisecondes',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Table des logs système';

